/* Procedure structure for procedure `update_holiday` */

/*!50003 DROP PROCEDURE IF EXISTS  `update_holiday` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `update_holiday`( year_id  INT  )
BEGIN    
update `date_info` di  
 LEFT JOIN  `holiday_year` hy ON di.`year`=year_id and  hy.year_id=di.`year` AND  di.`date_id`>=hy.`start_date_id` AND di.`date_id`<=hy.`end_date_id`
set   di.holiday_id=  ifnull( hy.`holiday_id`,0);
 COMMIT;
 END */$$
DELIMITER ;

